import Dropzone from 'react-dropzone'
import React, {useCallback} from 'react'
import { Container,Image } from 'react-bootstrap'
import UploadImage from '../Images/upload.png'
import { computerVision } from '../Service/service'
import $ from 'jquery'
import * as jquery from 'jquery'
function api(arrayBuffer)
{

// Replace the subscriptionKey string value with your valid subscription key.
  var subscriptionKey = "231ffed796a44ee1b3cae51827505a99";
  var uriBase = "https://germanywestcentral.api.cognitive.microsoft.com/vision/v1.0/RecognizeText";

  var params = {
    "handwriting": "true",
  };
  $.ajax({
    url: uriBase + "?" + $.param(params),

    beforeSend: function(jqXHR) {
      jqXHR.setRequestHeader("Content-Type", "application/octet-stream");
      jqXHR.setRequestHeader("Ocp-Apim-Subscription-Key", subscriptionKey);
    },

    type: "POST",
    processData: false,
    data: arrayBuffer
  })

  .done(function(data, textStatus, jqXHR) {
    // Show progress.
    console.log(data)
    setTimeout(function() {
      // The "Operation-Location" in the response contains the URI to retrieve the recognized text.
      var operationLocation = jqXHR.getResponseHeader("Operation-Location");

      $.ajax({
        url: operationLocation,
        beforeSend: function(jqXHR) {
          jqXHR.setRequestHeader("Content-Type", "application/json");
          jqXHR.setRequestHeader("Ocp-Apim-Subscription-Key", subscriptionKey);
        },
        type: "GET",
      })

      .done(function(data) {
        // Show formatted JSON on webpage.
        console.log(data)
      })
    }, 10000);
  })

  .fail(function(jqXHR, textStatus, errorThrown) {
    // Put the JSON description into the text area.
    console.log(textStatus)
    alert(1);
  })

}
export default function DropzoneContainer({setBuffer,setResult})
{
    const onDrop = useCallback((acceptedFiles) => {
        acceptedFiles.forEach((file) => {
          console.log(file)
         
          const reader = new FileReader()
          
        
         
          reader.onabort = () => console.log('file reading was aborted')
          reader.onerror = () => console.log('file reading has failed')
          reader.onload = () => {
          // Do whatever you want with the file contents
            console.log(reader)
            const binaryStr = reader.result
            console.log(binaryStr)
            setBuffer(binaryStr)
            console.log("ceva?")
            api(binaryStr)
          }
          reader.readAsArrayBuffer(file)
        })
        
      }, [])


    return <Container style={{height:"20rem",width:"20rem",border:"2px dotted white",padding:20}}>
    <Dropzone onDrop={onDrop}>
    {
      ({getRootProps, getInputProps}) => (
      <section style={{height:"100%",width:"100%",textAlign:"center",border:"2px solid white"}}>
        <div style={{height:"100%",width:"100%",position:"relative"}} {...getRootProps()}>
          <Image src={UploadImage} style={{width:180,height:180}}></Image> 
          <input  style={{height:"100%",width:"100%"}} {...getInputProps()} />
          <p style={{position:"absolute",top:"40%"}}>Drag and drop the pdf or the picture here, or click to select the file</p>
        </div>
      </section>
    )}
    </Dropzone>
    </Container>
}
