import { Container, Nav ,Row,Col,Image} from "react-bootstrap";
import Arrow from '../Images/arrow.png'
import DropzoneContainer from "./DropzoneContainer";
export default function Home()
{

    

    return (
        <Container >
        <Row style={{marginTop:20}}>
          <Col>
            <DropzoneContainer></DropzoneContainer>
          </Col>
          <Col md="auto" style={{textAlign:"center"}}>
            <Image src={Arrow} style={{width:300,height:300}}></Image>
          </Col>
          <Col>
          <textarea id="result" style={{width:"100%",height:"100%",backgroundColor:"#053742",color:"#E8F0F2",padding:10,border:"1px solid white",borderRadius:"5%"}}>
          At w3schools.com you will learn how to make a website. They offer free tutorials in all web development technologies.
          </textarea>
          </Col>
        </Row>
      </Container>
    );
}
