'use strict';

import { ComputerVisionClient } from '@azure/cognitiveservices-computervision';
import { ApiKeyCredentials } from '@azure/ms-rest-js';
import { createReadStream } from 'fs';
import { promisify } from 'util';

const sleep =promisify(setTimeout);

/**
 * AUTHENTICATE
 * This single client is used for all examples.
 */
const key = '231ffed796a44ee1b3cae51827505a99';
const endpoint = 'https://adriandiac.cognitiveservices.azure.com/';


const computerVisionClient = new ComputerVisionClient(
    new ApiKeyCredentials({ inHeader: { 'Ocp-Apim-Subscription-Key': key } }), endpoint);
// Prints all text from Read result


const STATUS_SUCCEEDED = "succeeded";
const STATUS_FAILED = "failed"

function printRecText(readResults,setResult) {
  let total='';
  console.log('Recognized text:');
  for (const page in readResults) {
    if (readResults.length > 1) {
      total+=`==== Page: ${page}`;
    }
    const result = readResults[page];
    if (result.lines.length) {
      for (const line of result.lines) {
        total+=line.words.map(w => w.text).join(' ');
      }
      setResult(total)
    }
    else { console.log('No recognized text.'); }
  }
}

async function readTextFromURL(client, bufferArray) {
  // To recognize text in a local image, replace client.read() with readTextInStream() as shown:
  let result = await client.readInStream(() => createReadStream(bufferArray))
  // Operation ID is last path segment of operationLocation (a URL)
  let operation = result.operationLocation.split('/').slice(-1)[0];

  // Wait for read recognition to complete
  // result.status is initially undefined, since it's the result of read
  while (result.status !== STATUS_SUCCEEDED) { await sleep(1000); result = await client.getReadResult(operation); }
  return result.analyzeResult.readResults; // Return the first page of result. Replace [0] with the desired page if this is a multi-page file such as .pdf or .tiff.
}


export async function computerVision(setResult,bufferArray) {

// URL images containing printed and/or handwritten text. 
// The URL can point to image files (.jpg/.png/.bmp) or multi-page files (.pdf, .tiff).
const printedTextSampleURL = 'https://moderatorsampleimages.blob.core.windows.net/samples/sample2.jpg';
const multiLingualTextURL = 'https://raw.githubusercontent.com/Azure-Samples/cognitive-services-sample-data-files/master/ComputerVision/Images/MultiLingual.png';
const mixedMultiPagePDFURL = 'https://raw.githubusercontent.com/Azure-Samples/cognitive-services-sample-data-files/master/ComputerVision/Images/MultiPageHandwrittenForm.pdf';



    


// Recognize text in printed image from a URL
  console.log('Read printed text from URL...', printedTextSampleURL.split('/').pop());
  const printedResult = await readTextFromURL(computerVisionClient, bufferArray);
  printRecText(printedResult,setResult);
}
