import { Container, Nav ,Row,Col,Image,Spinner} from "react-bootstrap";
import Arrow from '../Images/arrow.png'
import { useState } from "react";
import DropzoneContainer from "./DropzoneContainer";
function Render(buffer,result)
{

}
export default function Home()
{
    const [result, setResult] = useState(null);
    const [bufferArray,setBuffer]=useState(null)



    return (
        <Container >
        <Row style={{marginTop:20}}>
          <Col>
            <DropzoneContainer setBuffer={setBuffer} setResult={setResult}></DropzoneContainer>
          </Col>
          <Col style={{textAlign:"center"}}>
            <Image src={Arrow} style={{width:300,height:300}}></Image>
          </Col>
          <Col style={{textAlign:"center"}}>
          {
            
            bufferArray==null?<textarea id="result" style={{width:"100%",height:"100%",textAlign:"center",backgroundColor:"#053742",color:"#E8F0F2",padding:10,border:"1px solid white",borderRadius:"5%",margin:10}}>
            Here will be you result and from where you can copy 
            </textarea>:(result?
            <Spinner animation="border" style={{position:"absolute",top:"40%"}}/>:
            <div id="result" style={{width:"100%",height:"100%",textAlign:"center",backgroundColor:"#053742",color:"#E8F0F2",padding:10,border:"1px solid white",borderRadius:"5%",margin:10}}>
            {result}
            </div>)
          }
          
          </Col>
        </Row>
      </Container>
    );
}
