import Arrow from '../Images/arrow.png'
import { useState } from "react";
import DropzoneContainer from "./DropzoneContainer";
export default function Home()
{
    const [result, setResult] = useState(null);
    const [bufferArray,setBuffer]=useState(null)



    return (
        <Container >
        <Row style={{marginTop:20}}>
          <Col>
            <DropzoneContainer setBuffer={setBuffer}></DropzoneContainer>
          </Col>
          <Col style={{textAlign:"center"}}>
            <Image src={Arrow} style={{width:300,height:300}}></Image>
          </Col>
          <Col>
          <div> id="result" style={{width:"100%",height:"100%",textAlign:"center",backgroundColor:"#053742",color:"#E8F0F2",padding:10,border:"1px solid white",borderRadius:"5%",margin:10}}>
          {/* {
            bufferArray!=null?"Here will be you result and from where you can copy": */}
            <Spinner animation="border" />
          
          </div>
          </Col>
        </Row>
      </Container>
    );
}
